import customtkinter as ctk
from tkinter import filedialog, messagebox, ttk
from openpyxl import load_workbook, Workbook
from openpyxl.styles import PatternFill
import os
import sys
import base64
import io
from PIL import Image

# --- Configuração Visual ---
ctk.set_appearance_mode("Light") 
ctk.set_default_color_theme("blue") 

class SesiAuditor(ctk.CTk):
    def __init__(self):
        super().__init__()

        # Configurações da Janela
        self.title("Sistema de Auditoria Patrimonial - SESI Sorocaba")
        self.geometry("950x900") 
        self.minsize(800, 700)
        
        # Cores
        self.sesi_red = "#C62828"
        self.sesi_dark_red = "#8E0000"
        self.text_color = "#333333"

        # ====================================================================
        # COLE O NOVO CÓDIGO GIGANTE DO PNG ABAIXO
        # ====================================================================
        self.logo_base64 = """"""
        # ====================================================================

        # Variáveis
        self.filepaths = []
        self.scanned_codes = set()
        
        self.create_welcome_screen()

    # --- NOVA FUNÇÃO DE IMAGEM (CORRIGIDA PARA NÃO ESTICAR) ---
    def get_image_from_base64_proportional(self, base64_string, target_width):
        """
        Lê o código da imagem e a redimensiona mantendo a proporção original.
        Você só define a largura desejada, a altura é automática.
        """
        try:
            if "COLE_AQUI" in base64_string or len(base64_string) < 100:
                return None
            
            base64_string = base64_string.strip()
            image_data = base64.b64decode(base64_string)
            image_buffer = io.BytesIO(image_data)
            
            # 1. Abre a imagem original com PIL
            pil_image = Image.open(image_buffer)
            
            # 2. Pega o tamanho original
            orig_w, orig_h = pil_image.size
            
            # 3. Calcula a proporção para não esticar
            ratio = target_width / orig_w
            target_height = int(orig_h * ratio)
            
            # 4. Cria a imagem CTK com o tamanho proporcional calculado
            # Use o mesmo pil_image para light e dark mode para garantir transparência
            return ctk.CTkImage(light_image=pil_image, dark_image=pil_image, size=(target_width, target_height))
            
        except Exception as e:
            print(f"Erro imagem: {e}")
            return None

    def create_welcome_screen(self):
        self.welcome_frame = ctk.CTkFrame(self, fg_color="transparent")
        self.welcome_frame.pack(expand=True, fill="both")

        # Título
        ctk.CTkLabel(self.welcome_frame, text="AUDITOR DE PATRIMÔNIO", font=("Arial", 36, "bold"), text_color=self.sesi_red).pack(pady=(30, 5))
        ctk.CTkLabel(self.welcome_frame, text="Controle de Ativos - FABLAB SESI", font=("Arial", 18), text_color="gray").pack(pady=(0, 20))

        # LOGO (Proporcional)
        # Defini a largura como 400px. A altura será calculada sozinha.
        img = self.get_image_from_base64_proportional(self.logo_base64, target_width=400)
        
        if img:
            # fg_color="transparent" é CRUCIAL aqui para o fundo do label não aparecer
            ctk.CTkLabel(self.welcome_frame, text="", image=img, fg_color="transparent").pack(pady=10)
        else:
            lbl = ctk.CTkLabel(self.welcome_frame, text="[COLE O CÓDIGO DA LOGO PNG NO SCRIPT]", text_color="red", font=("Arial", 16, "bold"))
            lbl.pack(pady=20)

        # Botão Iniciar
        btn_start = ctk.CTkButton(
            self.welcome_frame, text="INICIAR SISTEMA", font=("Arial", 20, "bold"),
            height=60, width=300, fg_color=self.sesi_red, hover_color=self.sesi_dark_red,
            corner_radius=30, command=self.switch_to_main
        )
        btn_start.pack(pady=20)

        # CRÉDITOS
        credits_frame = ctk.CTkFrame(self.welcome_frame, fg_color="transparent")
        credits_frame.pack(pady=10)

        role_font = ("Arial", 12, "bold")
        name_font = ("Arial", 14)
        
        ctk.CTkLabel(credits_frame, text="Desenvolvedor:", font=role_font, text_color="gray").pack()
        ctk.CTkLabel(credits_frame, text="Rafael Henrique Prudencio", font=name_font, text_color=self.text_color).pack(pady=(0, 5))

        ctk.CTkLabel(credits_frame, text="Orientador:", font=role_font, text_color="gray").pack()
        ctk.CTkLabel(credits_frame, text="Marcelo Dias de Freitas Junior", font=name_font, text_color=self.text_color).pack(pady=(0, 5))

        ctk.CTkLabel(credits_frame, text="Técnico de Teatro:", font=role_font, text_color="gray").pack()
        ctk.CTkLabel(credits_frame, text="Leonardo Oliveira de Camargo", font=name_font, text_color=self.text_color).pack()

        # Rodapé
        self.create_footer(self.welcome_frame)

    def create_footer(self, parent_frame):
        footer_frame = ctk.CTkFrame(parent_frame, fg_color=self.sesi_red, corner_radius=0, height=50)
        footer_frame.pack(side="bottom", fill="x")
        ctk.CTkLabel(footer_frame, text="SESI SOROCABA - FABLAB", font=("Arial", 12, "bold"), text_color="white").pack(side="left", padx=20, pady=15)
        ctk.CTkLabel(footer_frame, text="Suporte: devprudencio@gmail.com", font=("Arial", 12, "bold"), text_color="white").pack(side="right", padx=20, pady=15)

    def switch_to_main(self):
        self.welcome_frame.destroy()
        self.create_main_interface()

    # --- (O RESTANTE DO CÓDIGO DA INTERFACE PRINCIPAL PERMANECE IGUAL) ---
    def create_main_interface(self):
        top_frame = ctk.CTkFrame(self, fg_color="#F9F9F9", corner_radius=10)
        top_frame.pack(fill="x", padx=20, pady=10)
        self.btn_load = ctk.CTkButton(top_frame, text="1. CARREGAR PLANILHAS (XLSX)", command=self.load_files, font=("Arial", 14, "bold"), fg_color="#333333", hover_color="#000000", width=250)
        self.btn_load.pack(pady=10)
        self.lbl_files = ctk.CTkLabel(top_frame, text="Nenhum arquivo carregado", text_color="gray")
        self.lbl_files.pack(pady=(0, 10))
        scan_frame = ctk.CTkFrame(self, fg_color="transparent")
        scan_frame.pack(pady=10)
        ctk.CTkLabel(scan_frame, text="BIPAR CÓDIGO AQUI:", font=("Arial", 16, "bold"), text_color=self.sesi_red).pack()
        self.entry_scan = ctk.CTkEntry(scan_frame, width=300, height=50, font=("Arial", 24), justify="center", border_color=self.sesi_red, border_width=2)
        self.entry_scan.pack(pady=5)
        self.entry_scan.bind('<Return>', self.on_scan)
        self.entry_scan.focus_set()
        tree_frame = ctk.CTkFrame(self, fg_color="transparent")
        tree_frame.pack(expand=True, fill="both", padx=20, pady=5)
        style = ttk.Style()
        style.theme_use("clam")
        style.configure("Treeview", background="#FFFFFF", foreground="black", rowheight=30, fieldbackground="#FFFFFF", font=("Arial", 12))
        style.configure("Treeview.Heading", font=("Arial", 12, "bold"), background="#E0E0E0")
        self.tree = ttk.Treeview(tree_frame, columns=('code',), show='headings', height=10)
        self.tree.heading('code', text='ITENS LIDOS')
        self.tree.column('code', anchor='center')
        self.tree.pack(side="left", expand=True, fill="both")
        sb = ctk.CTkScrollbar(tree_frame, command=self.tree.yview)
        sb.pack(side="right", fill="y")
        self.tree.configure(yscrollcommand=sb.set)
        self.lbl_count = ctk.CTkLabel(self, text="Total lido: 0", font=("Arial", 14, "bold"))
        self.lbl_count.pack(pady=5)
        self.btn_process = ctk.CTkButton(self, text="2. PROCESSAR E GERAR RELATÓRIO", command=self.process_all_files, font=("Arial", 16, "bold"), fg_color=self.sesi_red, hover_color=self.sesi_dark_red, height=50)
        self.btn_process.pack(fill="x", padx=40, pady=20)

    def load_files(self):
        filenames = filedialog.askopenfilenames(title="Selecione as Planilhas", filetypes=[("Excel files", "*.xlsx")])
        if filenames:
            self.filepaths = filenames
            qtd = len(filenames)
            self.lbl_files.configure(text=f"{qtd} arquivos prontos para análise.", text_color="green")
            self.entry_scan.focus_set()

    def on_scan(self, event):
        code = self.entry_scan.get().strip()
        if code:
            code_normalized = str(code)
            if code_normalized not in self.scanned_codes:
                self.scanned_codes.add(code_normalized)
                self.tree.insert('', 0, values=(code_normalized,))
                self.lbl_count.configure(text=f"Total lido: {len(self.scanned_codes)}")
            self.entry_scan.delete(0, 'end')

    def process_all_files(self):
        if not self.filepaths:
            messagebox.showwarning("Erro", "Selecione as planilhas primeiro!")
            return
        if not self.scanned_codes:
            messagebox.showwarning("Erro", "Nenhum código foi bipado!")
            return
        output_folder = "Relatorios_Gerados"
        if not os.path.exists(output_folder):
            os.makedirs(output_folder)
        green_fill = PatternFill(start_color="00FF00", end_color="00FF00", fill_type="solid")
        global_found_items = set()
        files_processed_count = 0
        try:
            for filepath in self.filepaths:
                wb = load_workbook(filepath)
                for sheet in wb.worksheets:
                    for row in sheet.iter_rows():
                        match_in_row = False
                        for cell in row:
                            if cell.value is not None:
                                cell_val = str(cell.value).strip()
                                if cell_val in self.scanned_codes:
                                    match_in_row = True
                                    global_found_items.add(cell_val)
                        if match_in_row:
                            for cell in row:
                                cell.fill = green_fill
                filename = os.path.basename(filepath)
                save_path = os.path.join(output_folder, f"VERIFICADO_{filename}")
                wb.save(save_path)
                files_processed_count += 1
            not_found_items = self.scanned_codes - global_found_items
            if not_found_items:
                wb_extras = Workbook()
                ws_extras = wb_extras.active
                ws_extras.title = "Itens Nao Encontrados"
                ws_extras.append(["Codigo Lido", "Status"])
                for item in not_found_items:
                    ws_extras.append([item, "NAO ENCONTRADO"])
                extras_path = os.path.join(output_folder, "RELATORIO_ITENS_SOBRANDO.xlsx")
                wb_extras.save(extras_path)
                msg_extra = f"\n\nATENÇÃO: {len(not_found_items)} itens sobraram."
            else:
                msg_extra = "\n\nSucesso Total!"
            messagebox.showinfo("Concluído", f"Processados: {files_processed_count}\nSalvos em: /{output_folder}" + msg_extra)
            os.startfile(output_folder)
        except Exception as e:
            messagebox.showerror("Erro Crítico", f"Falha: {str(e)}")

if __name__ == "__main__":
    app = SesiAuditor()
    app.mainloop()